    const user = await _User.default.findOne({
      username: body.username
    });

    if (!user) {
      ctx.body = {
        code: 404,
        msg: "请检查账号！"
      };
      return;
    }

    try {
      const key = (0, _uuid.v4)();
      (0, _RedisConfig.setValue)(key, _jsonwebtoken.default.sign({
        _id: user._id
      }, _config.default.JWT_SECRET, {
        expiresIn: "30m"
      }), 30 * 60); // body.username -> database -> email
      // 发送邮件

      let result = await (0, _MailConfig.default)({
        type: "reset",
        data: {
          key: key,
          username: body.username
        },
        expire: (0, _moment.default)().add(30, "minutes").format("YYYY-MM-DD HH:mm:ss"),
        // 过期时间
        email: body.username,
        user: user.name ? user.name : body.username
      });
      ctx.body = {
        code: 200,
        data: result,
        msg: "邮件发送成功"
      };
    } catch (e) {
      console.log(e);
    }
  }

  async login(ctx) {
    // 接收用户的数据
    const {
      body
    } = ctx.request;
    let sid = body.sid;
    let code = body.code; // 验证图片验证码的时效性，正确性

    if (await (0, _utils.checkCode)(sid, code)) {
      // 验证用户账号密码是否正确
      // mongoDB查库
      let checkUserPasswd = false;
      let user = await _User.default.findOne({
        username: body.username
      });

      if (await _bcrypt.default.compare(body.password, user.password)) {
        checkUserPasswd = true;
      }

      if (checkUserPasswd) {
        const userObj = user.toJSON();
        const arr = ["password", "username", "roles"];
        arr.map(item => delete userObj[item]); // 返回token

        let token = _jsonwebtoken.default.sign({
          _id: userObj._id
        }, _config.default.JWT_SECRET, {
          expiresIn: "1d" // 一天有效期

        }); // 加入isSign属性


        const signRecord = await _SignRecord.default.findByUid(userObj._id);

        if (signRecord) {
          if ((0, _moment.default)(signRecord.created).format("YYYY-MM-DD") === (0, _moment.default)().format("YYYY-MM-DD")) {
            userObj.isSign = true;
          } else {
            userObj.isSign = false;
          }

          userObj.lastSign = signRecord.created;
        } else {
          // 用户无签到记录
          userObj.isSign = false;
        }

        ctx.body = {
          code: 200,
          data: userObj,
          token: token
        };
      } else {
        // 用户名，密码验证失败，返回提示
        ctx.body = {
          code: 404,
          msg: "用户名或密码错误"
        };
      }
    } else {
      ctx.body = {
        code: 401,
        msg: "图片验证码错误"
      };
    }
  }

  async reg(ctx) {
    // 接受客户端的数据
    const {
      body
    } = ctx.request; // 校验验证码（时效性，有效性）

    let sid = body.sid;
    let code = body.code;
    let msg = {};
    let check = true;

    if (await (0, _utils.checkCode)(sid, code)) {
