    if (!body.key) {
      ctx.body = {
        code: 500,
        msg: "请求参数异常，请重新获取链接",
      };
      return;
    }
    if (!result) {
      msg.code = ["验证码已经失效，请重新获取！"];
      ctx.body = {
        code: 500,
        msg: msg,
      };
      return;
    }
    const token = await getValue(body.key);
    if (token) {
      const obj = getJWTPayload("Bearer " + token);
      body.password = await bcrypt.hash(body.password, 5);
      await User.updateOne(
        { _id: obj._id },
        {
          password: body.password,
        }
      );
      ctx.body = {
        code: 200,
        msg: "更新用户密码成功！",
      };
    } else {
      ctx.body = {
        code: 500,
        msg: "链接已经失效",
      };
    }
  }
}

